mapper.select:
SELECT *
FROM MEMBER
WHERE member_pw = #{member_pw} and member_id = #{member_id}


select member_id, member_pw 
from MEMBER
where member_id = #{member_id}

mapper.update:
UPDATE MEMBER
SET
last_access = #{last_access}
WHERE member_id = #{member_id}

UPDATE MEMBER
SET member_pw = #{member_pw}
WHERE member_id = #{member_id}
